RSpec.describe ProgramsController, type: :controller do
  render_views
  
  let(:user) { create :admin_user }
  let(:org) { create :organization }
  let(:program) { create(:program, attributes) }
  let(:attributes) { valid_attributes }
  # This should return the minimal set of attributes required to create a valid
  # Program. As you add validations to Program, be sure to
  # adjust the attributes here as well.
  let(:valid_attributes) { attributes_for(:program).merge(organization_id: org.id) }
  let(:invalid_attributes) { { name: '' } }

  # This should return the minimal set of values that should be in the session
  # in order to pass any filters (e.g. authentication) defined in
  # ProgramsController. Be sure to keep this updated too.
  let(:valid_session) { {} }
  
  describe 'when logged in' do
    before do
      sign_in user
    end

    describe "GET #index" do
      it "returns a success response" do
        get :index, params: {}, session: valid_session
        expect(response).to be_successful
      end
    end

    describe "GET #new" do
      it "returns a success response" do
        get :new, params: {}, session: valid_session
        expect(response).to be_successful
      end
    end

    describe "GET #edit" do
      it "returns a success response" do
        get :edit, params: {id: program.to_param}, session: valid_session
        expect(response).to be_successful
      end
    end

    describe "POST #create" do
      subject(:post_create) do
        post(
          :create,
          params: {
            program: attributes,
            session: valid_session 
          }
        )
      end

      context "with valid params" do
        it "creates a new Program" do
          expect { post_create }.to change(Program, :count).by(1)
        end

        it "redirects to the program list" do
          post_create
          expect(response).to redirect_to(programs_path)
        end
      end

      context "with invalid params" do
        let(:attributes) { invalid_attributes }
        it "returns a success response (i.e. to display the 'new' template)" do
          post_create
          expect(response).to be_successful
        end
      end
    end

    describe "PUT #update" do
      subject(:put_update) do
        put(
          :update,
          params: {
            id: program.to_param,
            program: put_attributes,
            session: valid_session
          }
        )
      end

      context "with valid params" do
        let(:put_attributes) {
          { name: 'turtles' }
        }
        before(:each) { put_update }

        it "updates the requested program" do
          program.reload
          expect(program.name).to eq('turtles')
        end

        it "redirects to the program" do
          expect(response).to redirect_to(programs_path)
        end
      end

      context "with invalid params" do
        let(:put_attributes) { invalid_attributes }
        before(:each) { put_update }

        it "returns a success response (i.e. to display the 'edit' template)" do          
          expect(response).to be_successful
        end
      end
    end

    describe "DELETE #destroy" do
      it "destroys the requested program" do
        program = create(:program, valid_attributes)
        expect {
          delete :destroy, params: {id: program.to_param}, session: valid_session
        }.to change(Program, :count).by(-1)
      end

      it "redirects to the programs list" do
        program = Program.create! valid_attributes
        delete :destroy, params: {id: program.to_param}, session: valid_session
        expect(response).to redirect_to(programs_url)
      end
    end
  end
  
  describe 'JSON requests' do
    let(:access_token) { create :access_token }
    
    describe "GET #index" do
      it "allows access token via params" do
        get :index, params: {access_key: access_token.key}, session: valid_session, format: :json
        expect(response).to be_successful
      end

      it "allows access token via headers" do
        request.headers.merge!('Access-Key' => access_token.key)
        
        get :index, params: {}, session: valid_session, format: :json
        expect(response).to be_successful
      end
    end
  end
end
